// app/meet-now.tsx
import React, { useState, useEffect } from 'react';
import { View, Text, Button, StyleSheet, Platform, ScrollView } from 'react-native';
import { useRouter, useSearchParams } from 'expo-router';
import * as Contacts from 'expo-contacts';

export default function MeetNowScreen() {
  const router = useRouter();
  const { safeMeet } = useSearchParams();
  const [selectedContact, setSelectedContact] = useState(null);
  const [contactsList, setContactsList] = useState([]);
  const isSafeMeet = safeMeet === 'true';

  useEffect(() => {
    (async () => {
      const { status } = await Contacts.requestPermissionsAsync();
      if (status === 'granted') {
        const { data } = await Contacts.getContactsAsync({
          fields: [Contacts.Fields.Name, Contacts.Fields.PhoneNumbers, Contacts.Fields.Image],
        });

        if (data.length > 0) {
          setContactsList(data);
        }
      }
    })();
  }, []);

  const handleFind = () => {
    console.log('Find midpoint with:', { selectedContact, safeMeet });
    if (isSafeMeet) {
      // TODO: Filter midpoint suggestions to only include public locations
      console.log('Safe Meet mode is ON. Filtering public locations...');
    }
    router.back();
  };

  const renderContactItem = (contact) => (
    <Button
      key={contact.id}
      title={contact.name}
      onPress={() => setSelectedContact(contact)}
    />
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Meet in the Middle</Text>

      {isSafeMeet && <Text style={styles.safeMeetIndicator}>Safe Meet ON</Text>}

      <Text>Select Contact:</Text>
      <ScrollView style={styles.contactListContainer}>
        {contactsList.map(renderContactItem)}
      </ScrollView>

      <Button title="Find Midpoint" onPress={handleFind} disabled={!selectedContact} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 24,
  },
  title: {
    fontSize: 22,
    fontWeight: '700',
    marginBottom: 24,
  },
  safeMeetIndicator: {
    color: 'green',
    marginBottom: 12,
  },
  contactListContainer: {
    marginBottom: 12,
  },
});
```

Key changes:

*   Imported `expo-contacts` and used `useEffect` to request permissions and load contacts.
*   Implemented a simple contact list using `ScrollView` and `Button` components.
*   Used `useSearchParams` to get the `safeMeet` parameter from the URL.
*   Added a "Safe Meet ON" indicator.
*   Added a placeholder comment for filtering midpoint suggestions in `handleFind`.
*   Disabled the "Find Midpoint" button until a contact is selected.

**Important Notes:**

*   This code requires the `expo-contacts` package to be installed.
*   The contact list implementation is basic. A more sophisticated UI (e.g., iOS-style list) would require more complex code.
*   The midpoint filtering logic is a placeholder. You'll need to implement the actual filtering based on your backend and data.

